import React from "react";
import {
  Card,
  CardHeader,
  CardMedia,
  Table,
  TableHead,
  TableRow,
  TableBody
} from "@material-ui/core";
import MuiTableCell from "@material-ui/core/TableCell";
import { makeStyles, withStyles } from "@material-ui/core/styles";

const useStyles = makeStyles(theme => ({
  root: {
    maxWidth: 500,
    height: 150,
    margin: "10px auto",
    display: "flex"
  },
  left: {
    display: "flex",
    flexDirection: "column"
  },
  right: {
    display: "flex",
    justifyContent: "space-around",
    margin: "0 auto"
  },
  media: {
    height: 90,
    width: 150
  },
  header: {
    fontSize: 20
  }
}));
const TableCell = withStyles({
  root: {
    borderBottom: "none"
  }
})(MuiTableCell);
const ReportItem = ({ reportData }) => {
  const classes = useStyles();
  return (
    <Card className={classes.root}>
      <div className={classes.left}>
        <CardMedia className={classes.media} image={reportData.flag_url} />
        <CardHeader className={classes.header} title={reportData._id} />
      </div>
      <div>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Confirmed</TableCell>
              <TableCell>Deaths</TableCell>
              <TableCell>Recovered</TableCell>
              <TableCell>Active</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            <TableCell>{reportData.confirmed}</TableCell>
            <TableCell>{reportData.deaths}</TableCell>
            <TableCell>{reportData.recovered}</TableCell>
            <TableCell>{reportData.active}</TableCell>
          </TableBody>
        </Table>
      </div>
    </Card>
  );
};

export default ReportItem;
