import React, { useEffect, useState } from "react";
import ReportItem from "./ReportItem";
import { connect } from "react-redux";
import * as actions from "../../actions/index";
import SearchBar from "./SearchBar";

const ReportList = props => {
  const [selectedOption, setSelectedOption] = useState("");
  const [dataDisplay, setDataDisplay] = useState([]);
  useEffect(() => {
    props.fetchDetailedReport();
  }, []);

  const countryChange = value => {
    if (value) {
      setSelectedOption(value._id);
      //   const dataDisplay =
      // selectedOption !== ""
      //   ? props.reportList.filter(report => report._id === selectedOption)
      //   : props.reportList;
      setDataDisplay(
        props.reportList.filter(report => report._id === selectedOption)
      );
    } else {
      setDataDisplay(props.reportList);
    }
  };
  //   const dataDisplay =
  //     selectedOption !== ""
  //       ? props.reportList.filter(report => report._id === selectedOption)
  //       : props.reportList;
  console.log(dataDisplay);
  return (
    <div>
      <SearchBar
        country={props.reportList}
        handleCountryChange={countryChange}
      />
      {dataDisplay.map((report, idx) => {
        return <ReportItem key={idx} reportData={report} />;
      })}
    </div>
  );
};

const mapStateToProps = state => ({
  reportList: state.reports.data
});

const mapActionToProps = {
  fetchDetailedReport: actions.fetchDetailedReport
};

export default connect(mapStateToProps, mapActionToProps)(ReportList);
